import * as i0 from "@angular/core";
export class CardComponent {
    constructor() {
        this.toggle = false;
        this.users = [];
        this.sortUser = () => {
            this.users = [];
            allUsersData.value.sort((a, b) => a.author_name !== b.author_name ? a.author_name < b.author_name ? (!toggle.value ? -1 : 1) : (!toggle.value ? 1 : -1) : 0);
            allUsersData.value.slice([0], [10]).map((item) => {
                users.value.push(item);
            });
            toggle.value = !toggle.value;
        };
        // retrieve all users info
        this.fetchAllUsers = async () => {
            await axios
                .get(`http://localhost:3000/users`)
                .then((response) => {
                const data = response.data; // [{}, {}]
                allUsersData.value = data;
                data.slice([0], [10]).map((item) => {
                    users.value.push(item); // display only 10 cards intially
                });
            });
        };
    }
    ngOnInit() {
    }
}
